import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom"; // Link added for the login link
import axios from "axios";

// NOTE: Icon variables are REMOVED as requested, solving the image issues.

const Register = () => {
    // --- 1. Form State Management (CRUCIAL WEEK 2 UPDATE) ---
    const [formData, setFormData] = useState({
        username: "",
        email: "",
        password: "",
        department: "", // <-- NEW: Required by DB Schema
        role: "employee", // <-- NEW: Default role is employee
    });
    const navigate = useNavigate();
    const [error, setError] = useState("");
    const [successMessage, setSuccessMessage] = useState(""); // For clean success messages

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError("");
        setSuccessMessage("");
        
        try {
            // NOTE: Replace the URL below with your actual FastAPI registration endpoint if different
            const { data } = await axios.post("http://localhost:8000/auth/register", formData);
            
            console.log("Registration successful:", data);
            
            // CRITICAL FIX: Use state for success message instead of illegal alert()
            setSuccessMessage("Registration successful! Redirecting to login...");
            
            // Redirect after a slight delay to allow message to show
            setTimeout(() => {
                navigate("/login"); 
            }, 1500);

        } catch(error){
            console.error("Registration error:", error);
            if (error.response && error.response.data && error.response.data.detail) {
                setError(error.response.data.detail);
            } else {
                setError("Network error or server unavailable. Please try again.");
            }
        }
    };

    // --- 2. Component JSX (Clean Tailwind Styling without Icons) ---
    return (
        <div className="min-h-screen flex items-center justify-center bg-gray-100">
            <div className="bg-white p-8 rounded-xl shadow-2xl w-full max-w-lg"> 

                <h2 className="text-3xl font-extrabold text-center mb-8 text-blue-700">Join BragBoard</h2>
                
                {/* Error and Success Messages */}
                {error && <p className="text-red-500 text-center mb-4 font-medium">{error}</p>}
                {successMessage && <p className="text-green-600 text-center mb-4 font-medium">{successMessage}</p>}

                <form onSubmit={handleSubmit} className="space-y-4">
                    
                    {/* Username Field (Icon removed) */}
                    <div className="border border-gray-300 rounded-lg p-3 transition duration-150 hover:border-blue-400">
                        <input type="text" 
                            name="username" 
                            placeholder="Full Name / Username" 
                            value={formData.username} 
                            onChange={handleChange} 
                            className="w-full focus:outline-none"
                            required 
                        />
                    </div>
                    
                    {/* Email Field (Icon removed) */}
                    <div className="border border-gray-300 rounded-lg p-3 transition duration-150 hover:border-blue-400">
                        <input type="email" 
                            name="email" 
                            placeholder="Work Email" 
                            value={formData.email}
                            onChange={handleChange}
                            className="w-full focus:outline-none"
                            required 
                        />
                    </div>

                    {/* Department Field (NEW REQUIRED FIELD - No Icon) */}
                    <div className="border border-gray-300 rounded-lg p-3 transition duration-150 hover:border-blue-400">
                        <input type="text" 
                            name="department" 
                            placeholder="Your Department (e.g., Marketing, Tech)" 
                            value={formData.department} 
                            onChange={handleChange}
                            className="w-full focus:outline-none" 
                            required 
                        />
                    </div>

                    {/* Password Field (Icon removed) */}
                    <div className="border border-gray-300 rounded-lg p-3 transition duration-150 hover:border-blue-400">
                        <input type="password" 
                            name="password" 
                            placeholder="Password" 
                            value={formData.password} 
                            onChange={handleChange}
                            className="w-full focus:outline-none" 
                            required 
                        />
                    </div>
                    
                    {/* Register Button */}
                    <button 
                        type="submit"
                        className="w-full bg-blue-600 hover:bg-blue-700 text-white py-3 mt-6 rounded-md font-semibold transition duration-200 shadow-md hover:shadow-lg"
                    >
                        Register Account
                    </button>
                </form>

                {/* Link to Login */}
                <p className="text-center text-sm text-gray-600 mt-6">
                    Already have an account? <Link to="/login" className="text-blue-600 font-medium hover:underline">Log in here</Link>
                </p>
            </div>
        </div>
    );
};

export default Register;
