import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from "axios"; // Required for real API call

// NOTE: All icon variables and imports are removed entirely, as requested.

const Login = () => {
    // --- 1. Form State Management ---
    const [formData, setFormData] = useState({
        email: "",
        password: ""
    });
    const navigate = useNavigate();
    const [error, setError] = useState(null);

    const handleFormChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    // --- 2. Login Submission Logic (Connects to FastAPI) ---
    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);

        try {
            // Replace with your actual FastAPI endpoint URL
            const response = await axios.post("http://localhost:8000/auth/login", formData);
            
            // Assuming FastAPI returns a token object like { access_token: "..." }
            const token = response.data.access_token;

            // Save the received JWT token
            localStorage.setItem('access_token', token);
            
            // Redirect to the home dashboard (Week 2 Goal)
            navigate('/'); 

        } catch (err) {
            console.error("Login failed:", err);
            // Display error message from the backend if available
            setError(err.response?.data?.detail || "Login failed. Check credentials.");
        }
    };


    // --- 3. The Visual Component (Clean Tailwind CSS Layout) ---
    return (
        <div className="min-h-screen flex items-center justify-center bg-gray-100">
            <div className="bg-white p-8 rounded-lg shadow-xl w-full max-w-md">
                <h2 className="text-3xl font-bold text-center text-gray-800 mb-6">Employee Login</h2>
                
                {/* Error Message Display */}
                {error && <p className="text-red-500 text-center mb-4">{error}</p>}
                
                <form onSubmit={handleSubmit} className="space-y-4">
                    
                    {/* Email Field (NO ICON) */}
                    <div className="border border-gray-300 rounded-lg p-2">
                        <input
                            type="email"
                            name="email"
                            placeholder="Work Email"
                            onChange={handleFormChange}
                            required
                            className="w-full focus:outline-none p-1"
                        />
                    </div>
                    
                    {/* Password Field (NO ICON) */}
                    <div className="border border-gray-300 rounded-lg p-2">
                        <input
                            type="password"
                            name="password"
                            placeholder="Password"
                            onChange={handleFormChange}
                            required
                            className="w-full focus:outline-none p-1"
                        />
                    </div>

                    {/* Submit Button */}
                    <button
                        type="submit"
                        className="w-full bg-blue-600 text-white py-2 rounded-lg font-semibold hover:bg-blue-700 transition"
                    >
                        Login
                    </button>
                </form>

                {/* Link to Register */}
                <p className="text-center text-sm text-gray-600 mt-4">
                    Don't have an account? <Link to="/register" className="text-blue-600 font-medium hover:underline">Register here</Link>
                </p>
            </div>
        </div>
    );
};

export default Login;
