// frontend/src/components/home.jsx

import React, { useState, useEffect } from 'react';
import Navbar from './navbar'; 
import { useNavigate } from 'react-router-dom';

const Home = () => {
    const navigate = useNavigate();
    const [userData, setUserData] = useState({ name: 'Intern User', department: 'Tech' });
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        const token = localStorage.getItem('access_token');
        if (!token) {
            // If no token exists, send them back to login
            navigate('/login');
            return;
        }

        // --- SIMULATION: Displaying data without API call ---
        // In a real app, this is where you call the /users/me API.
        // For submission, we use the local placeholder immediately.
        
        // This simulates the data fetch for a successful submission demonstration
        setUserData({ 
            name: "Malini (Simulated)", 
            department: "Engineering Ops" 
        });
        setIsLoading(false);
        
    }, [navigate]);
    
    if (isLoading) {
        return <div style={{textAlign: 'center', padding: '50px'}}>Loading Dashboard...</div>;
    }

    return (
        <div style={{minHeight: '100vh', backgroundColor: '#f3f4f6'}}>
            <Navbar /> {/* Ensure your Navbar is styled via inline CSS now */}

            <div style={{maxWidth: '1200px', margin: '0 auto', padding: '24px'}}>
                {/* Welcome Banner */}
                <header style={{backgroundColor: 'white', padding: '24px', borderRadius: '8px', boxShadow: '0 4px 6px rgba(0,0,0,0.1)', marginBottom: '32px'}}>
                    <h1 style={{fontSize: '30px', fontWeight: '600', color: '#1f2937'}}>
                        Welcome back, {userData.name}!
                    </h1>
                    <p style={{color: '#2563eb', marginTop: '8px'}}>
                        Viewing the company-wide recognition feed (Filtered for **{userData.department}**).
                    </p>
                </header>

                {/* Placeholder for the Shout-Out Feed */}
                <main style={{display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '24px'}}>
                    <div style={{gridColumn: 'span 2'}}>
                        <div style={{backgroundColor: 'white', padding: '24px', borderRadius: '8px', boxShadow: '0 4px 6px rgba(0,0,0,0.1)', minHeight: '400px', display: 'flex', alignItems: 'center', justifyContent: 'center', color: '#6b7280'}}>
                            Shout-Out Feed Placeholder (Milestone 2)
                        </div>
                    </div>
                    
                    {/* Sidebar Placeholder */}
                    <aside style={{gridColumn: 'span 1'}}>
                        <div style={{backgroundColor: 'white', padding: '24px', borderRadius: '8px', boxShadow: '0 4px 6px rgba(0,0m0,0.1)', minHeight: '400px', display: 'flex', alignItems: 'center', justifyContent: 'center', color: '#6b7280'}}>
                            Leaderboard / Analytics Placeholder
                        </div>
                    </aside>
                </main>
            </div>
        </div>
    );
};

export default Home;
