import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from "axios";

const Login = () => {
    // --- 1. Form State Management ---
    const [formData, setFormData] = useState({
        email: "",
        password: ""
    });
    const navigate = useNavigate();
    const [error, setError] = useState(null);

    const handleFormChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    // --- 2. Login Submission Logic (Connects to FastAPI) ---
    // frontend/src/components/login.jsx

// ... handleSubmit function ...
    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);

        // --- SIMULATION: REPLACE API CALL WITH DIRECT SUCCESS LOGIC ---
        try {
            // 1. Simulate API call delay (optional)
            await new Promise(resolve => setTimeout(resolve, 500));
            
            // 2. Save a dummy token (simulating success)
            localStorage.setItem('access_token', 'SIMULATED_JWT_TOKEN');
            
            // 3. Redirect to the home dashboard
            navigate('/'); 

        } catch (err) {
            // In case of actual network failure, keep this error handler
            setError("Login attempt failed. Please check credentials.");
            console.error(err);
        }
    };
// ... rest of the file
    // --- 3. The Visual Component (FIXED: Using Inline Styles) ---
    return (
        <div style={{minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', backgroundColor: '#f3f4f6'}}>
            <div style={{backgroundColor: 'white', padding: '32px', borderRadius: '12px', boxShadow: '0 20px 25px -5px rgba(0,0,0,0.1), 0 10px 10px -5px rgba(0,0,0,0.04)', width: '100%', maxWidth: '400px'}}>
                
                <h2 style={{fontSize: '24px', fontWeight: '700', textAlign: 'center', color: '#1f2937', marginBottom: '24px'}}>Employee Login</h2>
                
                {error && <p style={{color: 'red', textAlign: 'center', marginBottom: '16px'}}>{error}</p>}
                
                <form onSubmit={handleSubmit} style={{display: 'flex', flexDirection: 'column', gap: '16px'}}>
                    
                    {/* Email Field */}
                    <input type="email" name="email" placeholder="Work Email" onChange={handleFormChange} required
                        style={{padding: '12px', border: '1px solid #d1d5db', borderRadius: '6px', width: '100%'}} />
                    
                    {/* Password Field */}
                    <input type="password" name="password" placeholder="Password" onChange={handleFormChange} required
                        style={{padding: '12px', border: '1px solid #d1d5db', borderRadius: '6px', width: '100%'}} />

                    {/* Submit Button */}
                    <button type="submit"
                        style={{width: '100%', backgroundColor: '#2563eb', color: 'white', padding: '12px', borderRadius: '6px', fontWeight: '600', cursor: 'pointer', marginTop: '8px'}}>
                        Login
                    </button>
                </form>

                {/* Link to Register */}
                <p style={{textAlign: 'center', fontSize: '14px', color: '#4b5563', marginTop: '16px'}}>
                    Don't have an account? <Link to="/register" style={{color: '#2563eb', fontWeight: '500'}}>Register here</Link>
                </p>
            </div>
        </div>
    );
};

export default Login;
