import React from 'react';
import Navbar from './navbar'; 
// Note: We are not importing Register or Login components here, 
// as they should be handled by your main App.js routing.


const Home = () => {
  // --- Start: Placeholder Data (To be replaced by real data in the next task) ---
  const userName = "Employee Name"; 
  const userDepartment = "Tech Dept";
  // --- End: Placeholder Data ---

  return (
    // Outer container: sets minimum height to fill the screen and a light gray background
    <div className="min-h-screen bg-gray-100">
      
      {/* 1. Navigation Bar (Header) */}
      <Navbar /> 

      {/* Main Content Area: Centered container with padding */}
      <div className="container mx-auto p-6">
        
        {/* 2. Welcome Banner */}
        <header className="bg-white p-6 rounded-lg shadow-lg mb-8">
          <h1 className="text-3xl font-semibold text-gray-800">
            Welcome back, {userName}!
          </h1>
          <p className="text-blue-600 mt-2">
            Viewing the company-wide recognition feed (Filtered for {userDepartment}).
          </p>
        </header>

        {/* 3. Main Feed Structure (Placeholders for Weeks 3-4) */}
        <main className="grid grid-cols-1 md:grid-cols-3 gap-6">
          
          {/* Main Feed Column (2/3 width on medium screens and up) */}
          <div className="md:col-span-2">
            <div className="bg-white p-6 rounded-lg shadow-md h-96 flex items-center justify-center text-gray-500">
              Shout-Out Post Form & Feed Placeholder (Milestone 2)
            </div>
          </div>
          
          {/* Sidebar/Leaderboard Column (1/3 width on medium screens and up) */}
          <aside className="md:col-span-1">
            <div className="bg-white p-6 rounded-lg shadow-md h-96 flex items-center justify-center text-gray-500">
              Leaderboard / Analytics Placeholder (Milestone 4)
            </div>
          </aside>
        </main>
      </div>
    </div>
  );
};

export default Home;
