import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from "axios";

// Define the correct backend URL prefix. The full path will be used in the fetch call.
// We'll assume the token endpoint is directly on the root API base for simplicity, 
// or at a configured endpoint like /token. Let's use /auth/token for demonstration.
// NOTE: Adjust this if your FastAPI token route is different (e.g., /token)
const API_BASE_URL = "http://127.0.0.1:8000"; 
const TOKEN_ENDPOINT = `${API_BASE_URL}/auth/token`; // Example: /auth router with a /token endpoint

const Login = () => {
    // --- 1. State Management ---
    const [formData, setFormData] = useState({
        email: "",
        password: ""
    });
    const navigate = useNavigate();
    const [error, setError] = useState(null);
    const [isLoading, setIsLoading] = useState(false);

    const handleFormChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
        setError(null); 
    };

    // Helper function to convert JSON to URL-encoded form data string
    const createFormUrlEncoded = (email, password) => {
        // FastAPI's standard token endpoint expects 'username' and 'password' keys, 
        // with 'username' typically being the email in a web context.
        const params = new URLSearchParams();
        params.append('username', email); 
        params.append('password', password);
        return params.toString();
    };

    // --- 2. Login Submission Logic ---
    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);
        setIsLoading(true);

        try {
            // Prepare the data in the required URL-encoded format
            const data = createFormUrlEncoded(formData.email, formData.password);

            // Send POST request to the backend's token endpoint.
            const response = await axios.post(
                TOKEN_ENDPOINT, 
                data, // Send URL-encoded data
                {
                    headers: {
                        // Crucially, set the Content-Type header to the required form type
                        'Content-Type': 'application/x-www-form-urlencoded',
                    }
                }
            );

            // Assuming the backend sends back { access_token: '...', token_type: 'bearer' }
            const token = response.data.access_token; 
            
            if (token) {
                // Store the received token locally
                localStorage.setItem('access_token', token);
                // Redirect to the dashboard, which is likely '/' or '/dashboard'
                navigate('/dashboard'); // Using '/dashboard' as per common practice
            } else {
                setError("Login successful, but token missing. Contact support.");
            }

        } catch (err) {
            // Handle errors (e.g., 401 Unauthorized or Network errors)
            if (err.response) {
                // Common FastAPI error detail for invalid credentials
                const detail = err.response.data.detail;
                const errorMessage = (detail === "Unauthorized") 
                    ? "Invalid email or password." 
                    : detail || "Login failed due to server error.";
                setError(errorMessage);
            } else {
                setError("Network error or server unavailable. Please try again.");
            }
        } finally {
            setIsLoading(false); // Stop loading
        }
    };
    
    // --- 3. The Visual Component (Tailwind-like styling maintained) ---
    return (
        <div className="min-h-screen flex items-center justify-center bg-gray-100">
            <div className="bg-white p-8 rounded-xl shadow-2xl w-full max-w-md">
                
                <h2 className="text-2xl font-bold text-center text-gray-900 mb-6">Employee Login</h2>
                
                {/* Error Display */}
                {error && <p className="text-red-500 text-sm text-center mb-4 p-2 bg-red-50 rounded-md border border-red-200">{error}</p>}
                
                <form onSubmit={handleSubmit} className="flex flex-col gap-4">
                    
                    {/* Email Field */}
                    <input 
                        type="email" 
                        name="email" 
                        placeholder="Work Email" 
                        value={formData.email} 
                        onChange={handleFormChange} 
                        required
                        className="p-3 border border-gray-300 rounded-lg focus:ring-blue-500 focus:border-blue-500 transition duration-150"
                        disabled={isLoading} 
                    />
                    
                    {/* Password Field */}
                    <input 
                        type="password" 
                        name="password" 
                        placeholder="Password" 
                        value={formData.password} 
                        onChange={handleFormChange} 
                        required
                        className="p-3 border border-gray-300 rounded-lg focus:ring-blue-500 focus:border-blue-500 transition duration-150"
                        disabled={isLoading} 
                    />

                    {/* Submit Button */}
                    <button 
                        type="submit"
                        className={`w-full py-3 rounded-lg font-semibold text-white mt-2 transition duration-200 
                            ${isLoading ? 'bg-blue-300 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-700'}`
                        }
                        disabled={isLoading}
                    >
                        {isLoading ? 'Logging In...' : 'Login'}
                    </button>
                </form>

                {/* Link to Register */}
                <p className="text-center text-sm text-gray-600 mt-6">
                    Don't have an account? <Link to="/register" className="text-blue-600 hover:text-blue-800 font-medium">Register here</Link>
                </p>
            </div>
        </div>
    );
};

export default Login;
