import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from "axios";

// Define the correct backend URL based on your configuration
// -------------------------------------------------------------------
// ðŸ”¥ THE FIX: INCLUDE THE BACKEND ROUTER PREFIX IN THE API_URL
// -------------------------------------------------------------------
const API_URL = "http://127.0.0.1:8000/auth"; 

const Login = () => {
    // --- 1. State Management ---
    const [formData, setFormData] = useState({
        email: "",
        password: ""
    });
    const navigate = useNavigate();
    const [error, setError] = useState(null);
    const [isLoading, setIsLoading] = useState(false); // New state for loading

    const handleFormChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
        // Clear any previous error when the user starts typing
        setError(null); 
    };

    // --- 2. Login Submission Logic ---
    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);
        setIsLoading(true);

        try {
            // Send POST request to the backend's login endpoint.
            // Note: The URL is now ${API_URL}/login, which resolves to:
            // http://127.0.0.1:8000/auth/login
            const response = await axios.post(`${API_URL}/login`, 
                formData,
                {
                    headers: {
                        'Content-Type': 'application/json',
                    }
                }
            );

            // Assuming the backend sends back a JWT token in the response data
            const token = response.data.access_token; 
            
            if (token) {
                // Store the received token locally
                localStorage.setItem('access_token', token);
                // Redirect to the home dashboard
                navigate('/'); 
            } else {
                setError("Login successful, but token missing. Contact support.");
            }


        } catch (err) {
            // Handle errors from the API call (400, 401, Network errors)
            if (err.response) {
                const errorMessage = err.response.data.detail || "Invalid email or password.";
                setError(errorMessage);
            } else {
                // If you get this error now, check your backend server status again.
                setError("Network error or server unavailable. Please try again.");
            }
        } finally {
            setIsLoading(false); // Stop loading regardless of success or failure
        }
    };
    
    // --- 3. The Visual Component (No changes needed here) ---
    return (
        <div style={{minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', backgroundColor: '#f3f4f6'}}>
            <div style={{backgroundColor: 'white', padding: '32px', borderRadius: '12px', boxShadow: '0 20px 25px -5px rgba(0,0,0,0.1), 0 10px 10px -5px rgba(0,0,0,0.04)', width: '100%', maxWidth: '400px'}}>
                
                <h2 style={{fontSize: '24px', fontWeight: '700', textAlign: 'center', color: '#1f2937', marginBottom: '24px'}}>Employee Login</h2>
                
                {/* Error Display */}
                {error && <p style={{color: 'red', textAlign: 'center', marginBottom: '16px'}}>{error}</p>}
                
                <form onSubmit={handleSubmit} style={{display: 'flex', flexDirection: 'column', gap: '16px'}}>
                    
                    {/* Email Field */}
                    <input type="email" name="email" placeholder="Work Email" value={formData.email} onChange={handleFormChange} required
                        style={{padding: '12px', border: '1px solid #d1d5db', borderRadius: '6px', width: '100%'}} 
                        disabled={isLoading} />
                    
                    {/* Password Field */}
                    <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleFormChange} required
                        style={{padding: '12px', border: '1px solid #d1d5db', borderRadius: '6px', width: '100%'}}
                        disabled={isLoading} />

                    {/* Submit Button */}
                    <button 
                        type="submit"
                        style={{
                            width: '100%', 
                            backgroundColor: isLoading ? '#93c5fd' : '#2563eb', 
                            color: 'white', 
                            padding: '12px', 
                            borderRadius: '6px', 
                            fontWeight: '600', 
                            cursor: isLoading ? 'not-allowed' : 'pointer', 
                            marginTop: '8px'
                        }}
                        disabled={isLoading}
                    >
                        {isLoading ? 'Logging In...' : 'Login'}
                    </button>
                </form>

                {/* Link to Register */}
                <p style={{textAlign: 'center', fontSize: '14px', color: '#4b5563', marginTop: '16px'}}>
                    Don't have an account? <Link to="/register" style={{color: '#2563eb', fontWeight: '500'}}>Register here</Link>
                </p>
            </div>
        </div>
    );
};

export default Login;
